<?phpnamespace app\login\validate;use think\Validate;class AdminLogin extends Validate{    protected $rule =[        'auname'=>'require|alphaNum|length:6,20',        'aupwd'=>'require|length:8,20',        'autname'=>'require|length:2,20',        'aupwd1'=>'require|length:8,20|confirm:aupwd'    ];    protected $message=[        'auname'=>'用户名必填|长度在六到二十位之间|仅含字母和数字',        'aupwd'=>'密码必填|长度在八到二十位之间',        'autname'=>'真实姓名必填',    ];    protected $scene = [        'adminLogin'=>['auname','aupwd','autname'],        'adminLoginRePwd'=>['aupwd','aupwd1']    ];}